import SearchResults from "../../components/SearchResults/SearchResults";

let accessToken;
const clientID = '6fcae6e681ed465d9b593ad989a8b758';
const redirectURI = 'http://localhost:3000'
let Spotify = {
    getAccessToken() {
        if(accessToken) return accessToken;
        else {
            let urlInBrowser = window.location.href;
            let accessTokenMatch = urlInBrowser.match(/access_token=([^&]*)/);
            let expiresInMatch = urlInBrowser.match(/expires_in=([^&]*)/);
            let expiresIn;
            if(accessTokenMatch) {
                let accessToken = accessTokenMatch[1];
                expiresIn = expiresInMatch[1];
                window.setTimeout( () => accessToken = '', expiresIn * 1000);
                window.history.pushState('Access Token', null, '/');
                return accessToken;
            }
            else {
                window.location = `https://accounts.spotify.com/authorize?client_id=${clientID}&response_type=token&scope=playlist-modify-public&redirect_uri=${redirectURI}`;
                alert('Spotify auth successful!');
            }


        }
    },

    search(searchTerm) {
        return fetch(`https://api.spotify.com/v1/search?type=track&q=${searchTerm}`, 
        {
            headers: { Authorization: `Bearer ${accessToken}`}
        }).then(response => {
            if(response.ok) {
                return response.json();
            }
            else {
                throw(new Error('Something has done wrong'));
            }
        }).then(responseJson => {
            responseJson.map(track => {
                return {
                    id: track.id,
                    name: track.name,
                    artist: track.artists[0].name,
                    album: track.album.name,
                    uri: track.uri
                }

            })
        })
    },

    savePlaylist(playlistName, playlistURIs) {
        if(playlistURIs && playlistName) {
            let accessTokenVar = this.getAccessToken; // se dà problemi, cambiare con accessToken diretto
            let headers = { Authorization: `Bearer: ${accessTokenVar}` };
            let userID;
            fetch('https://api.spotify.com/v1/me', headers)
                .then(response => {
                    if(response.ok) {
                        return response.json();
                    }
                    else {
                        alert('error!');
                    }
                })
                .then(responseJson => {
                    userID = responseJson.id;
                })

        } else return undefined;


    }
}

export default Spotify;
