let accessToken;

function checkUrl() {
    let test = window.location.href.match(/access_token=([^&]*)/);
    if(test) accessToken = test;

}
let Spotify = {
    getAccessToken() {
        if(accessToken) return accessToken;
        else {
            checkUrl();


        }


    }
}

console.log('https://accounts.spotify.com/authorize?client_id=6fcae6e681ed465d9b593ad989a8b758&redirect_uri=http://localhost:3000&response_type=token'.match())

