import React from 'react';
import './SearchBar.css';

class SearchBar extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            searchTerm: 'a'
        }
        this.search = this.search.bind(this);
        this.handleTermChange = this.handleTermChange.bind(this);
    }

    search() {
        this.props.onSearch(this.state.searchTerm);


        window.location.replace('https://accounts.spotify.com/authorize?client_id=6fcae6e681ed465d9b593ad989a8b758&redirect_uri=http://localhost:3000&response_type=token');
        let newUrl = window.location.href;
        let accessToken = newUrl.match('/access_token=([^&]*)/');
        let expiryTime = newUrl.match('/expires_in=([^&]*)/');
        alert(accessToken, expiryTime);
    }

    handleTermChange(e) {
        this.setState({ 
            searchTerm: e.target.value
        });

    }
    render() {
        return (
            <div className="SearchBar">
                <input placeholder="Enter A Song, Album, or Artist" onChange={this.handleTermChange}  />
                <button className="SearchButton" onClick={this.search}>SEARCH</button>
            </div>
        )
    }
}

export default SearchBar;
