import React from 'react';
import './App.css';
import SearchResults from '../SearchResults/SearchResults';
import SearchBar from '../SearchBar/SearchBar';
import Playlist from '../Playlist/Playlist';

class App extends React.Component {

  constructor(props) {

    super(props);

    this.state = {
      searchResults: [
        {
          name: 'Faith',
          artist: 'Swae Lee',
          album: 'Creed 2',
          id: 1,
        },
        {
          name: 'Still Be Friends',
          artist: 'Tyga, G-Eazy, Tory Lanez',
          album: 'Single',
          id: 2,
        }
      ],

      playlistName: 'My Playlist name',

      playlistTracks: [
        {
          name: 'testname',
          artist: 'testartist',
          album: 'testalbum',
          id: 0
        }
      ]
    }

    this.addTrack = this.addTrack.bind(this);

  }

  addTrack(track) {
    let isInPlaylistTracks = false;
    this.state.playlistTracks.forEach(trackObj => {
      if (trackObj.id === track.id) isInPlaylistTracks = true;
    });

    if (!isInPlaylistTracks) {
      this.setState({ playlistTracks: [...this.state.playlistTracks, track] });
      isInPlaylistTracks = false;
    }
  }

  removeTrack(track) {
    let newObj = this.state.playlistTracks.filter(trackObj => trackObj.id !== track.id);



  }

  render() {
    return (
      <div>
        <h1>Ja<span className="highlight">mmm</span>ing</h1>
        <div className="App">
          <SearchBar />
          <div className="App-playlist">
            <SearchResults searchResults={this.state.searchResults} onAdd={this.addTrack} />
            <Playlist playlistName={this.state.playlistName} playlistTracks={this.state.playlistTracks} />
          </div>
        </div>
      </div>
    )
  };
}

export default App;
