let accessToken;
const clientID = '6fcae6e681ed465d9b593ad989a8b758';
const redirectURI = 'http://localhost:3000'

let Spotify = {
    getAccessToken() {
        if (accessToken) {
            return accessToken;
        }
        else {
            let urlInBrowser = window.location.href;
            let accessTokenMatch = urlInBrowser.match(/access_token=([^&]*)/);
            let expiresInMatch = urlInBrowser.match(/expires_in=([^&]*)/);
            let expiresIn;
            if (accessTokenMatch) {
                accessToken = accessTokenMatch[1];
                expiresIn = expiresInMatch[1];
                window.setTimeout(() => accessToken = '', expiresIn * 1000);
                window.history.pushState('Access Token', null, '/');
                console.log('Auth token successfully captured!')
                return accessToken;
            }
            else {
                window.location = `https://accounts.spotify.com/authorize?client_id=${clientID}&response_type=token&scope=playlist-modify-public&redirect_uri=${redirectURI}`;
                console.log('Redirecting to Spotify auth screen');
            }


        }
    },

    search(searchTerm) {
        return fetch(`https://api.spotify.com/v1/search?type=track&q=${searchTerm}`,
            {
                headers: { 'Authorization': `Bearer ${this.getAccessToken()}` }
            }).then(response => {
                if (response.ok) {
                    console.log('Successfully got a response for the tracks')
                    return response.json();
                }
                else {
                    throw (new Error('Something has gone wrong while fetching the tracks'));
                }
            }).then(responseJson => {
                console.log('Fetching the response JSON');
                console.log(responseJson);
                return responseJson.tracks.items.map(track => {
                    let obj = {
                        id: track.id,
                        name: track.name,
                        artist: track.artists[0].name,
                        album: track.album.name,
                        uri: track.uri
                    }
                    return obj;
                })
            })
    },

    savePlaylist(playlistName, playlistURIs) {
        if (playlistURIs && playlistName) {
            let accessTokenVar;
            if (accessToken) {
                accessTokenVar = accessToken
            } else {
                this.getAccessToken();
                accessTokenVar = accessToken;
            }
            alert(accessTokenVar);
            let userID;

            return fetch('https://api.spotify.com/v1/me',
                {
                    headers: { 'Authorization': `Bearer ${this.getAccessToken()}` }
                })
                .then(response => {
                    console.log('check this out');
                    console.log(response);
                    alert('the response from the playlist creation was ' + response.ok);
                    return response.json();
                })
                .then(responseJson => {
                    userID = responseJson.id;
                    console.log(`I have assigned this userid ${userID}`);
                })
                .then( () => {
                return fetch(`https://api.spotify.com/v1/users/${userID}/playlists`,
                {
                    method: 'POST',
                    headers: {
                        'Authorization': `Bearer ${this.getAccessToken()}`,
                        'Content-Type': 'application/json',
                    },
                    body: JSON.stringify({
                        name: playlistName

                    }),
                })})
                .then(playlistResponse => {
                    console.log('the playlist response obj was ' + playlistResponse.ok);
                    return playlistResponse.json();
                    /*                     console.log('Mi spieghi che cazzo restituisci');
                    console.log(playlistResponse); */
                    /*                     console.log('Here');
                                        console.log(playlistResponse);
                                        alert('playlistResponse is ' + playlistResponse);

                                        if(playlistResponse.ok) {
                                            return playlistResponse.json();
                                        }
                                        else {
                                            throw(Error('Error while fetching playlist creation response.'));
                                        } */

                })
                .then(playlistResponseJson => {
                    console.log('Sono arrivato fino a qui');
                    console.log(playlistResponseJson.name);
                    console.log(playlistURIs);
                    fetch(`https://api.spotify.com/v1/playlists/${playlistResponseJson.id}/tracks`,
                        {
                            method: 'POST',
                            headers: {
                                'Authorization': `Bearer ${this.getAccessToken()}`,
                                'Content-Type': 'application/json',
                            },
                            body: JSON.stringify({
                                uris: playlistURIs,
                            })
                        })
                })

        } else alert('missing something');
    }
}

export default Spotify;
