let accessToken;

function checkUrl() {
    let test = window.location.href.match(/access_token=([^&]*)/);
    if(test) accessToken = test;

}
let Spotify = {
    getAccessToken() {
        if(accessToken) return accessToken;
        else {
            checkUrl();


        }


    }
}
let url = 'https://example.com/callback#access_token=NwAExz...BV3O2Tk&token_type=Bearer&expires_in=3600&state=123';
url.match(/access_token=([^&]*)/);



export default Spotify;
